<?php if ( ! defined( 'FW' ) ) {    die( 'Forbidden' );}global $wp;$current_url = home_url(add_query_arg(array(),$wp->request));if($atts['containe_class'] != ''){    echo '<div class="'.$atts['containe_class'].'">';} if($atts['list_class'] != ''){    echo '<ul class="'.$atts['list_class'].'">';}else{    echo '<ul>';}foreach ( fw_akg( 'item', $atts, array() ) as $item ) :    $class_active = '';    if($item['item_links'] != ''){        if($item['item_links'] == $current_url){	        $class_active = 'active';        }    }    if($item['item_class'] != ''){        echo '<li class="'.$item['item_class'].' '.$class_active.'">';    }else{        if($class_active != ''){            echo '<li class="'.$class_active.'">';        }else{            echo '<li>';        }    }    if($item['item_links'] != ''){        echo '<a href="'.$item['item_links'].'" target="'.$item['item_target'] .'">';            if($item['item_image'] != '' || $item['item_image']){                echo '<img src="'.$item['item_image']['url'].'">';            }            if($item['icon']){                echo '<i class="'.esc_attr($item['icon']).'"></i>';            }            echo '<span>'.$item['item_title'].'</span>';        echo '</a>';    }else{        if($item['icon']){            echo '<span>';            echo '<i class="'.esc_attr($item['icon']).'"></i>';            echo $item['item_title'];            echo '</span>';        }else{ echo $item['item_title'];}        if($item['item_image'] != '' || $item['item_image']){            echo '<img src="'.$item['item_image']['url'].'">';        }    }    echo '</li>';    endforeach;    echo '</ul>';if($atts['containe_class'] != ''){    echo '</div>';}?>