<?phpfunction wiz_scripts() {//    wp_enqueue_style( 'wiz-style', get_stylesheet_uri() );    wp_enqueue_script( 'jquery.sticky', get_template_directory_uri() . '/js/jquery.sticky.min.js', array('jquery'), '0.1', true );//    wp_enqueue_script( 'wiz-jquery-scrolltofixed-min', get_template_directory_uri() . '/js/jquery-scrolltofixed-min.js', array('jquery'), '0.1', true );    wp_enqueue_script( 'owl.carousel.min', get_template_directory_uri() . '/js/owl.carousel.min.js', array('jquery'), '2.3.4', true );    wp_enqueue_script( 'owl.carousel2.thumbs.min', get_template_directory_uri() . '/js/owl.carousel2.thumbs.min.js', array('jquery'), '2.3', true );    wp_enqueue_script( 'jquery.validate', 'https://cdnjs.cloudflare.com/ajax/libs/jquery-validate/1.15.0/jquery.validate.js', array('jquery'), '1.15', true );    wp_enqueue_script( 'wiz-site', get_template_directory_uri() . '/js/site-main.js', array('jquery'), '0.1', true );//    if ( is_singular() && comments_open() && get_option( 'thread_comments' ) ) {//        wp_enqueue_script( 'comment-reply' );//    }}add_action( 'wp_enqueue_scripts', 'wiz_scripts' );function remove_jquery_migrate( $scripts ) {    if ( ! is_admin() && isset( $scripts->registered['jquery'] ) ) {        $script = $scripts->registered['jquery'];        if ( $script->deps ) {            $script->deps = array_diff( $script->deps, array( 'jquery-migrate' ) );        }    }}add_action( 'wp_default_scripts', 'remove_jquery_migrate' );function footer_enqueue_scripts() {    remove_action('wp_head', 'wp_print_scripts');    remove_action('wp_head', 'wp_print_head_scripts', 9);    remove_action('wp_head', 'wp_enqueue_scripts', 1);    add_action('wp_footer', 'wp_print_scripts', 5);    add_action('wp_footer', 'wp_enqueue_scripts', 5);    add_action('wp_footer', 'wp_print_head_scripts', 5);}add_action('after_setup_theme', 'footer_enqueue_scripts');?>